package oops;

public class ddfff {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,l=6;
		int x = 10;
		float y = 11.7669f;
	    float z = x+y;
		System.out.println(z);
		for (i=0;i<l;i++) 
		{
			for (j=0;j<=i;j++)
			{
				System.out.print("* ");
			}
			System.out.println(" ");
		}
		
			

	}

}
