package oops;

public class iff {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0;i<=10;i++) {
			if (i == 6) {
				break;
				//continue;
			}
			System.out.println(i);
		}
		System.out.println("number is greater than 5 so the loop got terminated");

	}

}
